    static readonly byte[] table = new byte[] { 0, 2, 90, 127, 166 };

    Texture2D ReadTexture()
    {
        const int width = 32;
        const int height = 64;

        Texture2D texture = new(width, height) 
        {
            name = "Texture",
            filterMode = FilterMode.Point,
        };
        
        for (int y = 0; y < height; ++y)
        {
            for (int x = 0; x < width; ++x)
            {
                int idx = y * width + x;
                if (data[idx] == 0)
                {
                    texture.SetPixel(x, y, new(0, 0, 0, 0));
                }
                else
                {
                    float l = (float)table[data[idx - 1]] / 255f;
                    texture.SetPixel(x, y, new(l, l, l));
                }
            }
        }
        
        texture.Apply();
        
        return texture;
    }
